import React, { useState } from 'react';
import {
  View,
  Text,
  FlatList,
  Image,
  Alert,
  Pressable,
  StyleSheet,
} from 'react-native';
import * as ImagePicker from 'expo-image-picker';
import { initializeApp } from 'firebase/app';
import { getAnalytics } from 'firebase/analytics';
import {
  getStorage,
  ref,
  uploadBytes,
  deleteObject,
  list,
} from 'firebase/storage';
import bg from './assets/bg.jpg';

const ImagePickerExample = () => {
  const [imageUri, setImageUri] = useState(bg);
  const [uploading, setUploading] = useState(false);
  const [image, setImage] = useState([null]);
  const [visible, setVisible] = useState(false);
  const [selectedImageUri, setSelectedImageUri] = useState(null); // Novo estado para armazenar a URI da imagem selecionada

  const firebaseConfig = {
    // sua configuração do Firebase aqui
  };

  // Inicializar o Firebase
  const app = initializeApp(firebaseConfig);
  const analytics = getAnalytics(app);

  // Função para selecionar uma imagem da galeria
  const pickImage = async () => {
    const result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });

    if (!result.cancelled) {
      setImageUri(result.uri);
    }
  };

  // Função para fazer upload da imagem para o Firebase
  const uploadImage = async () => {
    if (!imageUri) {
      Alert.alert('Selecione uma imagem antes de enviar.');
      return;
    }

    const storage = getStorage();
    const name = getRandom(200);
    const mountainsRef = ref(storage, name + '.jpg');

    const response = await fetch(imageUri);
    const blob = await response.blob();

    uploadBytes(mountainsRef, blob).then((snapshot) => {
      console.log(snapshot);
      alert('Imagem enviada com sucesso!!');
    });
  };

  // Função para listar as imagens armazenadas no Firebase
  const linkImage = async () => {
    const storage = getStorage();
    const listRef = ref(storage);

    const firstPage = await list(listRef, { maxResults: 100 });
    const lista = firstPage.items.map((item) => {
      return 'https://firebasestorage.googleapis.com/v0/b/' +
        item.bucket +
        '/o/' +
        encodeURIComponent(item.fullPath) +
        '?alt=media';
    });

    setImage(lista);
    setVisible(true);
  };

  // Função para deletar uma imagem do Firebase
  const deleteImage = async (imageRef) => {
    try {
      const storage = getStorage();
      const imageRefObj = ref(storage, imageRef);
      await deleteObject(imageRefObj);
      console.log('Imagem deletada com sucesso');
    } catch (error) {
      console.log('Erro ao deletar imagem', error);
    }
  };

  // Renderizar cada item da lista de imagens
  const renderItem = ({ item }) => (
    <View style={styles.item}>
      <Pressable onPress={() => setSelectedImageUri(item)}>
        <Image source={{ uri: item }} style={{ width: 50, height: 50 }} />
      </Pressable>
    </View>
  );

  return (
    <View style={styles.container}>
      <Pressable style={styles.button} onPress={pickImage}>
        <Text style={styles.textButton}>Selecionar Imagem</Text>
      </Pressable>

      {imageUri && (
        <Image
          source={{ uri: imageUri }}
          style={{ width: 200, height: 200, marginVertical: 20 }}
        />
      )}

      {uploading ? (
        <ActivityIndicator size="large" color="#0000ff" />
      ) : (
        <Pressable
          style={styles.button}
          onPress={uploadImage}
          disabled={!imageUri}
        >
          <Text style={styles.textButton}>Enviar Imagem</Text>
        </Pressable>
      )}

      <View style={{ marginVertical: 8 }} />

      <Pressable style={styles.button} onPress={linkImage}>
        <Text style={styles.textButton}>Ver Imagens</Text>
      </Pressable>

      <View style={{ paddingTop: 24 }} />

      {visible && (
        <FlatList
          style={{ width: '100%', paddingHorizontal: 50 }}
          data={image}
          keyExtractor={(item, index) => index.toString()}
          renderItem={renderItem}
        />
      )}

      {selectedImageUri && (
        <Image
          source={{ uri: selectedImageUri }}
          style={{ width: 300, height: 300, marginTop: 20 }}
        />
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    marginVertical: 24,
  },
  button: {
    width: 260,
    height: 40,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#17153B',
    borderRadius: 8,
    marginBottom: 10,
  },
  textButton: {
    fontSize: 16,
    color: '#fff',
    fontWeight: '700',
  },
  item: {
    marginBottom: 20,
    alignItems: 'center',
  },
});

export default ImagePickerExample;
